(20, 'choose_tpl', '', 0, 0, 0, '\r\n$n = '''';\r\nif($pagenumb==1)\r\n{ $n = "                <div class=\\"example_one\\">\r\n				<div class=\\"examples_items\\">\r\n					<div class=\\"examples_items_group\\">\r\n						<a class=\\"example_item examples_item1 gallery\\" data-fancybox-group=\\"gallery2\\"  href=\\"".$otz_photo_1."\\">\r\n							<img src=\\"".$otz_photo_1."\\" alt=\\"\\">\r\n						</a>\r\n						<a class=\\"example_item examples_item2 gallery\\" data-fancybox-group=\\"gallery2\\"  href=\\"".$otz_photo_2."\\">\r\n							<img src=\\"".$otz_photo_2."\\" alt=\\"\\">\r\n						</a>\r\n						<a class=\\"example_item examples_item3 gallery\\" data-fancybox-group=\\"gallery2\\"  href=\\"".$otz_photo_3."\\">\r\n							<img src=\\"".$otz_photo_3."\\" alt=\\"\\">\r\n						</a>\r\n						<a class=\\"example_item examples_item4 gallery\\" data-fancybox-group=\\"gallery2\\"  href=\\"".$otz_photo_4."\\">\r\n							<img src=\\"".$otz_photo_4."\\" alt=\\"\\">\r\n						</a>\r\n					</div>\r\n				</div>\r\n				<div class=\\"examples_review\\">\r\n					<div class=\\"examples_review_full\\">\r\n						<div class=\\"examples_review_from\\">\r\n							<span>".$address."</span>\r\n						</div>\r\n						<div class=\\"examples_review_who\\">\r\n							<span>".$name."</span>\r\n						</div>\r\n						<div class=\\"examples_review_full_photo\\">\r\n							<img src=\\"".$avatar."\\" alt=\\"".$name."\\">\r\n						</div>\r\n						<div class=\\"examples_review_full_text\\">\r\n							<p><span><strong>\\"".$zagl."\\"</strong></span></p>\r\n						".$content."\r\n                                                </div>\r\n					</div>\r\n				</div>\r\n                <div class=\\"clear\\"></div>\r\n                </div>";\r\n} else {\r\n$n ="                <div class=\\"example_one\\">\r\n				<div class=\\"examples_items\\">\r\n					<div class=\\"examples_items_group\\">\r\n						<a class=\\"example_item examples_item1 gallery\\" data-fancybox-group=\\"gallery1\\"  href=\\"".$otz_photo_1."